INSERT INTO contracts_table VALUES ('2', 'attachment_2', 'Приложение 2, если проезд только туда', '<p style=\"text-align: center;\" align=\"center\"><strong>{$hotel} {$city}</strong></p>\r\n<p style=\"text-align: center;\" align=\"center\"><strong>Территория</strong></p>\r\n<p style=\"text-align: center;\" align=\"center\">{$territory}</p>\r\n<p style=\"text-align: center;\" align=\"center\"><strong>Пляж</strong></p>\r\n<p style=\"text-align: center;\" align=\"center\">{$beach}</p>\r\n<p style=\"text-align: center;\" align=\"center\"><strong>Корпуса</strong></p>\r\n<p style=\"text-align: center;\" align=\"center\">{$housing}</p>\r\n<p style=\"text-align: center;\" align=\"center\"><strong>Номер</strong></p>\r\n<p style=\"text-align: center;\" align=\"center\">{$number}</p>\r\n<p style=\"text-align: center;\" align=\"center\"><strong>Вода</strong></p>\r\n<p style=\"text-align: center;\" align=\"center\">{$water}</p>\r\n<p style=\"text-align: center;\" align=\"center\"><strong>Питание</strong></p>\r\n<p style=\"text-align: center;\" align=\"center\">{$food}</p>\r\n<p style=\"text-align: center;\" align=\"center\"><strong>Отличительные особенности</strong></p>\r\n<p style=\"text-align: center;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>{$unique_features}</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span>Сроки тура: &nbsp;</span></strong>{$of_date}<span>&nbsp;- {$upto_date}</span></p>\r\n<p><span data-mce-mark=\"1\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><strong><span data-mce-mark=\"1\">Дата и время убытия из Минска</span></strong><strong><span data-mce-mark=\"1\">:</span></strong><span data-mce-mark=\"1\"> {$of_date}</span>&nbsp;-&nbsp;ул. Сурганова 61 (бизнес - центр &laquo;Зебра&raquo;) &ndash; в 06.30&nbsp;&nbsp;&nbsp; (время убытия указывается ориентировочно, точное время&nbsp; сообщается за сутки до выезда)</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong>&nbsp;Транспорт:</strong>&nbsp; автобус</p>\r\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Проживание:</strong>&nbsp; {$address_hotel}</p>\r\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Питание:&nbsp;</strong>чай, кофе (в дороге)</p>\r\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Маршрут:</strong>&nbsp; &nbsp; &nbsp;{$bus_route}</p>\r\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Трансфер:</strong>&nbsp; да</p>\r\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Программа тура:&nbsp;</strong>информационно-консультационные услуги,&nbsp; бронирование мест в пансионатах и гостиницах, транспортные услуги</p>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: center;\"><span>&nbsp;<strong>Примечание:</strong></span>&nbsp; &nbsp; {$note}</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ОДО &laquo;ИриАнна&raquo;&nbsp;&nbsp;&nbsp;</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>&nbsp;&nbsp;&nbsp;&nbsp;</strong>г. Минск ул. Сурганова, 61,оф.504 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;{$customer_name}&nbsp;&nbsp;</p>\r\n<p>&nbsp; тел. 292 04 02, факс 388 04 02</p>\r\n<p>&nbsp; р/с 3012014461250,</p>\r\n<p>&nbsp; ф-л № 514 АСБ &laquo;Беларусбанк&raquo;, код 614</p>\r\n<p>&nbsp; УНН 190135143, ОКПО37535316</p>');